import axios from "axios";
import { useEffect, useState } from "react";

function Banner(){
    let [detail,setDetail] = useState(undefined)

    useEffect(()=>{
        axios.get('https://api.themoviedb.org/3/movie/upcoming?api_key=1b189a59147c0e4a6d517737657a4a41')
        .then(function(res){
             console.log(res);
            
            // console.log(res.data.results[0].poster_path);
            let data = res.data.results[0]
            console.log(res.data.results[0])
            setDetail(data);
        })
    },[]);

    if(detail == undefined){
        return
    }
    return(
        <div className="h-[20vh] md:h-[70vh] bg-cover bg-center flex items-end" 
        style={{backgroundImage:`url(https://image.tmdb.org/t/p/original/${detail.poster_path})`}}>
            <div className="text-4xl text-white capitalize hover:uppercase bg-gray-600/40 w-full p-4 text-center text-bold ">
                {detail.title}
            </div>
        </div>
    )
}

export default Banner;
