import React from 'react'

export default function FlipCard({props}) {
    
    let {movieObj,vote_average,genreid,handleRemoveFromWatchList,name,watchList,poster_path} = props;
    
    
  return (
    <div className="cursor-pointer group perspective">
    <div className="relative preserve-3d group-hover:my-rotate-y-180 w-full h-full duration-1000">
      <div className="absolute backface-hidden w-full h-full">
        {/* back content */}
        <div class = "flex min-h-full flex-col items-center justify-center">
        <h1 class ="text-3xl font-bold">{movieObj.title}</h1>
        <p>{movieObj.popularity}</p>
        <p>{movieObj.vote_average}</p>
        <p>{genreid}</p>
        </div>

      </div>
      <div className="absolute my-rotate-y-180 backface-hidden w-full h-full overflow-hidden" >
        {/* front content */}
        <img className="" src={`https://image.tmdb.org/t/p/original/${movieObj.poster_path}`} alt="" />
        <div onClick={()=>handleRemoveFromWatchList(movieObj)}
            className="m-4 bg-gray-900
                        flex justify-center items-center 
                        h-8 w-8 rounded-lg">
                    &#128525;
                </div>
      </div>
    </div>
  </div>
  )
}
