import { Link } from "react-router-dom";


function Navbar(){
    return(
        <div className="flex items-center p-3 ">
            {/* clicking on logo will redirect back to the home */}
            <Link to={"/"} >
                <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/6/69/IMDB_Logo_2016.svg/2560px-IMDB_Logo_2016.svg.png" className="h-12 w-30" alt="" />
            </Link>
             {/* Redirect to watchlist */}
            <Link to={"/watchlist"} className="mx-6 text-3xl font-bold text-white hover:cursor-pointer">
                    WatchList
            </Link>
            <div>
            {!isContain(movieObj)?
           <div class="m-4 bg-yellow-300
           flex justify-center items-center 
           h-8 w-8 rounded-lg">
                            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" class="w-6 h-6"  onClick={()=>handleAddToWatchList(movieObj)} >
                <path fill-rule="evenodd" d="M12 2.25c-5.385 0-9.75 4.365-9.75 9.75s4.365 9.75 9.75 9.75 9.75-4.365 9.75-9.75S17.385 2.25 12 2.25ZM12.75 9a.75.75 0 0 0-1.5 0v2.25H9a.75.75 0 0 0 0 1.5h2.25V15a.75.75 0 0 0 1.5 0v-2.25H15a.75.75 0 0 0 0-1.5h-2.25V9Z" clip-rule="evenodd" />
                </svg>
                   
                </div>
           :<div 
            class="m-4 bg-gray-900
            flex justify-center items-center 
            h-8 w-8 rounded-lg">
                    &#128525;
                </div>
           } 
          
            </div>
        </div>
    )
}
export default Navbar;
