import MovieCard from "./MovieCard";
import axios from "axios"
import {useEffect, useState} from "react"
import Pagination from "./Pagination";
import SearchBar from "./SearchBar";

function Movies(props){
    let {watchList,setWatchList,
        handleAddToWatchList,handleRemoveFromWatchList,
        pageNo,handleNext,handlePrev} = props;

    let [movies,setMovies] = useState([]);
    const[allMovies,setAllMovies] = useState(null);
    const[searchKeyword,setSearchKeyword] = useState(null);
    const[Language,setLanguage]=useState(null);
    //sanity check can be done here
    const onSearchKeywordChange=(searchValue)=>{
        console.log(searchValue);
        setSearchKeyword(searchValue);
    }
    const onLanguageChange = (Language)=>{
        console.log(Language);
        setLanguage(Language);
    }
    useEffect(()=>{
            MovieAPiService.fetchMovies().then(response=>{
                if(response && response.length >0){
                    setAllMovies(response);
                }
            })
    },[]);

    useEffect(()=>{
        axios.get(`https://api.themoviedb.org/3/trending/movie/day?api_key=1b189a59147c0e4a6d517737657a4a41&page=${pageNo}`)
        .then(function(res){
        // console.log(res);
        // console.log(res.data.results);
        setMovies(res.data.results);
    })
    },[pageNo])

    

    return(
        <div className="p-5">
            <div className="text-2xl text-yellow-200 m-5 font-bold text-center ">
                Trending Movies
            </div>
            <div> <SearchBar onSearch = {onSearchKeywordChange} /> </div>
            <div className="flex flex-wrap justify-around gap-8">
                {movies.map((movieObj)=>{
                    // console.log(movieObj);
                    return <MovieCard key={movieObj.id}
                                    movieObj={movieObj}
                                    name={movieObj.title} 
                                    poster_path={movieObj.poster_path}
                                    watchList = { watchList}
                                    handleAddToWatchList = {handleAddToWatchList}
                                    handleRemoveFromWatchList = {handleRemoveFromWatchList}/>
                })}
            </div>
            <Pagination pageNo={pageNo}
                        handleNext={handleNext}
                        handlePrev={handlePrev}/>
        </div>
    )
}

export default Movies;
