import React from 'react';

const Dropdown = ({ genreList, handleFilter }) => (
  <select onChange={handleFilter}>
    {genreList.map((option, index) => (
      <option key={genre} value={genre}>
        {option}
      </option>
    ))}
  </select>
);
export default Dropdown;
